mapper.select:
select * from 
	(select rownum rnum, a.* from
		(select * from tbl_event
		) a)
where rnum between #{startRow} and #{endRow}

select * from 
    	(select rownum rnum, a.* from
			(select * from tbl_event
				where event_end_date < sysdate
				) a)
where rnum between #{startRow} and #{endRow}

mapper.update:
update tbl_event set
	event_count = event_count + 1
where event_no = #{event_no}
