mapper.select:
select * from (
	select rownum rnum, a.* from (
		select * from tbl_faq
		where faq_status = 'T'



	    			and question like '%' || #{keyword} || '%' 


	    			and answer like '%' || #{keyword} || '%' 



		order by reg_date asc
	) a
) where rnum between #{startRow} and #{endRow}

select * from (
	select rownum rnum, a.* from (
		select * from tbl_faq



	    			where question like '%' || #{keyword} || '%' 


	    			where answer like '%' || #{keyword} || '%' 



		order by reg_date asc
	) a
) where rnum between #{startRow} and #{endRow}

mapper.update:
update tbl_faq set
	question = #{question},
	answer = #{answer}
where faqno = #{faqno}

update tbl_faq set
	faq_status = 'F'
where faqno = #{faqno}
