mapper.select:
select * from tbl_user
where 
userid= #{userid} 
and userpw = #{userpw}
and user_status = '1'

mapper.update:
update tbl_user set
	point = point + #{point}
where userno = #{userno}

update tbl_user set
	profile_image = #{profile_image, jdbcType=VARCHAR}
where userno = #{userno}

update tbl_user 
	set user_status='0'
where userno = #{userno}
